import "./DataTable.css";
import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";
import axios from "axios";

const DataTable = () => {
  
  /* const [word, setWord] = useState([]);
  const [definition, setDefinition] = useState([]);
  const [example, setExample] = useState([]); */

  const [searchWord, setSearchWord] = useState([]);//search box 

  const [respArray,setRespArray] = useState([]) //inserting data on each search

  const getData = async ()=>{
    try{
      const resp = await axios.get(`https://api.dictionaryapi.dev/api/v2/entries/en_US/${searchWord}`);   
      
      console.log(resp.data[0].word);
      
      setRespArray([...respArray,resp.data])
            
      console.log(respArray);

     /*  setWord(resp.data[0].word)
      setDefinition(resp.data[0].meanings[0].definitions[0].definition)    
      setExample(resp.data[0].meanings[0].definitions[0].example) */
    }catch(err){
      console.log(err);
    }

    // setData(tableValues)

    
  }
  return (
    <>
      <h1>Dictionary</h1>
      <input type="text" autoFocus="on" placeholder="Search..." onChange={(e)=>setSearchWord(e.target.value)}/>
      <button className='btn btn-success' onClick={getData} >Submit</button>

      <div className="container mt-5">
        <div className="table-responsive">
          <table className="table table-hover">
            <thead className="thead-dark">
              <tr>
                <th>Sl No</th>
                <th>Word</th>
                <th>Definition</th>
                <th>Example</th>
              </tr>
            </thead>
            <tbody className="bg-#fff bg-gradient">                            
                <tr >
                    <td>{respArray.word}</td>
                    <td>{respArray.word}</td>
                    <td>{respArray.definition}</td>
                    <td>{respArray.example}</td>
                </tr>
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};
export default DataTable;
