import "./DataTable.css";
import "bootstrap/dist/css/bootstrap.min.css";
import React, { useEffect, useState } from "react";
import axios from "axios";

const DataTable = () => {
  const [data, setData] = useState("");
  const [searchWord, setSearchWord] = useState("");

  // calling the api
  useEffect(()=>{
    const getData = async ()=>{
        const resp = await axios.get(`https://api.dictionaryapi.dev/api/v2/entries/en_US/${searchWord}`);
        console.log(resp);
        // setData()       
    }
    getData();
  },[])

  return (
    <>
      <h1>Dictionary</h1>
      <input type="text" autoFocus="on" placeholder="Search..." onChange={(e)=>setSearchWord(e.target.value)}/>
      <button className='btn btn-success'>Submit</button>

      <div className="container mt-5">
        <div className="table-responsive">
          <table className="table table-hover">
            <thead className="thead-dark">
              <tr>
                <th>Sl No</th>
                <th>Word</th>
                <th>Definition</th>
                <th>Example</th>
              </tr>
            </thead>
            <tbody className="bg-#fff bg-gradient">
              {/* {
                                data.map((currElem,ind)=>{
                                    return(
                                      <tr key={ind}>
                                        <td>{currElem.slNo}</td>
                                        <td>{currElem.word}</td>
                                        <td>{currElem.Definition}</td>
                                        <td>{currElem.Example}</td>
                                      </tr>
                                    )
                                })
                            } */}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};
export default DataTable;
